import React, { Component } from 'react';

export default class PersonalData extends Component {
  render() {
    return (
        <div>
            <label htmlFor="name">Nome: </label>
            <input
                type="text"
                name='name'
                id='name'
                maxLength={40}
                required
                style={{textTransform: 'uppercase'}}
            />
            <br />
            <label htmlFor="email">Email: </label>
            <input 
                type="email"
                name="email"
                id="email"
                maxLength={50}
                required
            />
            <br />
            <label htmlFor="cpf">CPF: </label>
            <input
                type="text"
                name='cpf'
                id='cpf'
                minLength={11}
                maxLength={11}
                required
            />
            <br />
            <label htmlFor="adress">Endere√ßo: </label>
            <input
                type="text"
                name='adress'
                id='adress'
                maxLength={200}

                required
            />
        </div>
    );
  }
}
