import React, { Component } from 'react';

export default class PersonalData extends Component {
  render() {
    return (
        <div>
            <label htmlFor="name">Nome: </label>
            <input
            type="text"
            id='name'
            name='name'
            maxLength={40}
            required
            style={{textTransform: 'uppercase'}}
            />
            <br />
            <label htmlFor="email">Email: </label>
            <input 
            type="email"
            name="email"
            id="email"
            maxLength={50}
            required
            />
        </div>
    );
  }
}
