import { useEffect, useState } from "react";
import { listOfEmails } from "../helpers/mockEmailList";

export default function EmailList() {
    const [emailList, setEmailLIst] = useState([]);

    useEffect(() => setEmailLIst(listOfEmails), []);

  return (
    <div>
        <ul>
            {emailList.map(({id, title, status}) => {
                    const [isRead, setIsRead] = useState(status);
                useEffect(() => {
                    <li key={id} className={isRead === 1 ? 'read' : ''}>
                    <h3>{title}</h3>
                    <button type="button" onClick={() => isRead = 0} >&#128193;</button>
                    <button type="button" onClick={() => isRead = 1}>&#128194;</button>
                </li>
                }, [isRead]);
            //    return (

            //    )
            })}
        </ul>
    </div>
  );
};
