    const [emailList, setemailLIst] = useState([]);
    setemailLIst(listOfEmails);
  return (
    <div>
        <ul>
            {emailList.map(({id, title, status}) => {
               return (
               <li key={id}>
                    <h3>{title}</h3>
                    <button type="button">&#128193</button>
                    <button type="button">&#128194</button>
                </li>
               )
            })}
        </ul>
    </div>
  );
};
